<style lang="scss">
/* all clouds have white fill */
g[class*="cloud-"] {
    fill: white;
}

.cloud__bg__container {
    height: 100%;
    width: 100%;
    position: absolute;
    top: 0px;
    left: 0px;
    background: #8ed9ff;
    
    .cloud__bg__svg {
        height: 100%;
        width: 100%;
        opacity: 60%;
    }
}

.cloud-0 {
    animation: toright 40s infinite linear ;
}

.cloud-1 {
    animation: toright 30s infinite linear ;
}

.cloud-2 {
    animation: toright 20s infinite linear ;
}

.cloud-3 {
    animation: toright 10s infinite linear ;
}

@keyframes toright {
    0% {
        transform: translateX(-10%);
        opacity: 0%;
    }
    50% {
        opacity: 100%;
    }
    100% {
        transform: translateX(10%);
        opacity: 0%;
    }
}
</style>
